<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"><html xmlns="http://www.w3.org/1999/xhtml" xml:lang="pl-pl" lang="pl-pl" dir="ltr" ><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8"></head><style></style><? $visible_count = 8; ?><input id="visible_count" type="hidden" value="<? echo $visible_count; ?>"><script>function expand_collapse(){	var visible_count = document.getElementById("visible_count").value;	var span_expand = document.getElementById("expand");	//alert("zwin/rozwin " + visible_count);	span_expand.style.display = span_expand.style.display == "none" ? "inline" : "none";		for(var ii = visible_count; ii < 100; ii++)	{		var tr = document.getElementById("table_monitoring_tr_"+ii);		//alert(tr);		if (tr == null) break;		tr.style.display = tr.style.display == "table-row" ? "none" : "table-row" ;	}}</script><table id="table_monitoring" style="width: 100%;" cellspacing="0" cellpadding="0"><tr>	<th>lp.</th>	<th>Radny</th>	<th>		<abbr title="Komisje - Punkty uzyskane za obecność na danej Komisji w której znajduje się radny.">K</abbr>	</th>	<th>		<abbr title="Rady - Punkty uzyskane za obecność na posiedzeniach Rady Miasta.">R</abbr>	</th>	<th>		<abbr title="Dyżury - Punkty za obecność radnych na swoich dyżurach przeznaczonych na spotkania z mieszkańcami.">D</abbr>	</th>	<th>		<abbr title="Maile - punkty zyskane za odpowiedzi na pytania i uwagi wysyłane pocztą elektroniczną">M</abbr>	</th>	<th>		<abbr title="Internet - punkty za prowadzenie strony internetowej i/lub konta w serwisie społecznościowym.">I</abbr>	</th>	<th>		<abbr title="Suma - Suma punktów z wszystkich ocenianych kategorii, które dany radny uzyskuje po każdych 2 miesiącach trwania monitoringu">Suma</abbr>	</th></tr><? $gwiazdki_count = 1;$przypisy = "";foreach($model as $i=>$item){	$class = $i % 2 == 0 ? "even" : "odd";	?><tr class="<? echo $class; ?>" id="table_monitoring_tr_<? echo $i; ?>" <? if ($i >= $visible_count) echo "style='display:none;'" ?> ><?	echo "<td>".$item->RNK_LP.".</td>";	echo "<td><a href='".$this->createUrl("frontRadny/view&id=".$item->RNK_RDN_ID)."&tab=clubs' >";	if ($item->Radny->RDN_INFO_RNK != null && $item->Radny->RDN_INFO_RNK != "")	{		$gwiazdki = "";		for ($i = 0; $i < $gwiazdki_count; $i++)			$gwiazdki .= "*";					$gwiazdki = "<span class='asterix' >".$gwiazdki."</span> ";		echo $gwiazdki;		$przypisy .= "<p>".$gwiazdki." ".$item->Radny->ImieNazwisko()." ".$item->Radny->RDN_INFO_RNK."</p>";				$gwiazdki_count++;	}	echo $item->Radny->ImieNazwisko();	echo "</a></td>";	echo "<td>".$item->RNK_KMS."</td>";	echo "<td>".$item->RNK_RADY."</td>";	echo "<td>".$item->RNK_DUTY."</td>";	echo "<td>".$item->RNK_MAIL."</td>";	echo "<td>".$item->RNK_INTERNET."</td>";	echo "<th>".$item->RNK_SUM."</th>";	?></tr><?}?></table><div style="text-align: right;">	<a href="javascript:expand_collapse();">	<span id="expand" style="display: inline;">ro</span>zwiń</a></div><? echo $przypisy; ?></body></html>